mapper.select:
SELECT t.test_no testNo , t.test_title testTitle , t.test_date testDate , q.question_no questionNo , pa.answer paperAnser FROM test t LEFT OUTER JOIN question q ON t.test_no = q.test_no LEFT OUTER JOIN paper pa ON q.question_no = pa.question_no
test_title LIKE CONCAT('%',#{searchWord},'%')
GROUP BY testNo ORDER BY t.test_no DESC LIMIT #{beginRow}, #{rowPerPage}

mapper.insert:
INSERT INTO student (
	student_id
	, student_pw
	, student_name
) VALUES (
	#{studentId}
	, PASSWORD(#{studentPw})
	, #{studentName}
)

mapper.select:
SELECT student_no studentNo , student_id studentId , student_name studentName FROM student
student_name LIKE CONCAT('%',#{searchWord},'%')
ORDER BY student_no DESC LIMIT #{beginRow}, #{rowPerPage}
