mapper.update:
UPDATE question
SET question_title = #{newTitle}
WHERE question_no = #{questionNo}

UPDATE test
SET test_title = #{newTitle}
WHERE test_no = #{testNo}

mapper.select:
SELECT t.test_no testNo , t.test_title testTitle , t.test_date testDate , q.test_no questionCk FROM test t LEFT OUTER JOIN question q ON t.test_no = q.test_no
test_title LIKE CONCAT('%',#{searchWord},'%')
ORDER BY t.test_no DESC LIMIT #{beginRow}, #{rowPerPage}

mapper.insert:
INSERT INTO teacher (
	teacher_id
	, teacher_pw
	, teacher_name
) VALUES (
	#{teacherId}
	, PASSWORD(#{teacherPw})
	, #{teacherName}
)

mapper.select:
SELECT teacher_no teacherNo , teacher_id teacherId , teacher_name teacherName FROM teacher
teacher_name LIKE CONCAT('%',#{searchWord},'%')
ORDER BY teacher_no DESC LIMIT #{beginRow}, #{rowPerPage}
